mapper.update:
update department
set sn = #{sn},
  name = #{name},
  manager_id = #{manager.id},
  parent_id = #{parent.id}
where id = #{id}

mapper.select:
select id, sn, name, state, manager_id, parent_id
from department
where id = #{id}

select id, sn, name, state, manager_id, parent_id
from department
limit #{startIndex}, #{rows}
